constructor.chunk:
function(states) {

		this.music = states.music instanceof Music ? states.music : null;

	}

serialize.chunk:
function() {

			let states = {
				music: null
			};


			return {
				'constructor': 'lychee.policy.Music',
				'arguments':   [ states ]
			};

		}

sensor.chunk:
function() {

			let music  = this.music;
			let values = [ 0.5 ];


			if (music !== null) {

				let is_idle = music.isIdle;
				if (is_idle === true) {
					values[0] = 0;
				} else if (is_idle === false) {
					values[0] = 1;
				}

			}


			return values;

		}

control.chunk:
function(values) {

			let music = this.music;


			if (music !== null) {

				let is_idle = music.isIdle;
				if (is_idle === true && values[0] > 0.5) {
					music.play();
				} else if (is_idle === false && values[0] < 0.5) {
					music.stop();
				}

				return true;

			}


			return false;

		}
